/**
 * @function getSchoolByValue
 * @description Retrieves a school object from the SCHOOLS array by its 'value' property.
 * @param {string} value - The 'value' string of the school to find.
 * @returns {School | undefined} The matching School object or undefined if not found.
 */
export const getSchoolByValue = (value: string): School | undefined => {
  return SCHOOLS.find(school => school.value === value);
};

